sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Check if the status of the current task has changed
    if (current.status.changes()) {
        // Get the related goal record
        var goal = new GlideRecord('x_1563428_personal_goals'); // Replace with your Goals table name
        if (goal.get(current.goal)) { // Use the correct reference field

            // Query Goal Tasks for this specific goal
            var totalTasks = 0;
            var completedTasks = 0;
            var task = new GlideRecord('x_1563428_personal_goal_tasks'); // Replace with your Goal Tasks table name
            task.addQuery('goal', current.goal); // Filter tasks for the current goal
            task.query();

            while (task.next()) {
                totalTasks++; // Increment total task count
                if (task.status == 'completed') { // Adjust 'completed' to match your status value
                    completedTasks++; // Increment completed task count
                }
            }

            // Calculate the percentage of completed tasks
            var percentCompleted = totalTasks > 0 ? (completedTasks / totalTasks) * 100 : 0;

            // Update the percent_completed field in the Goal record
            goal.percent_completed = percentCompleted;
            goal.update();
        }
    }
})(current, previous);
